def populate_database():
    with app.app_context():
        db.create_all()
        
        existing_count = Plant.query.count()
        if existing_count > 0:
            print(f"Database already contains {existing_count} plants. Skipping population.")
            return
        
        print(f"Adding {len(plants_data)} plants to the database...")
        
        for plant_data in plants_data:
            plant = Plant(**plant_data)
            db.session.add(plant)
        
        db.session.commit()
        print(f"Successfully added {len(plants_data)} plants to the database!")

if __name__ == '__main__':
    populate_database()
